import React, {Component} from 'react';
import './Game.css';
import Point from './Point';
import Bricks from './Bricks';
import Pad from './Pad';

class Game extends Component {
  constructor(props) {
    super(props)
    this.pointLeft= 20;
    this.pointTop= 587;
    this.goRight=true;
    this.goDown=false;
    this.state = {
      displayLeft: this.pointLeft,
      displayTop: this.pointTop,
      brickWall: this.getBrickWall()
    }
    setInterval(this.MoovingBall(), 10)
  }

  deleteBricks=()=>{
    this.setState({brickWall:this.state.brickWall
      .filter( item => {
        return !(this.pointTop+20>item.top && this.pointTop<item.top+10 && this.pointLeft+20>item.left && this.pointLeft<item.left+65)
      })
      .map( item => {
        return item
      })})
  }

  generateIfCollideX = (left, top) => {
    return (this.pointTop+19>top && this.pointTop<top+9 && this.pointLeft+20>left && this.pointLeft<left+65)
  }

  generateIfCollideY = (left, top) => {
    return (this.pointTop+20>top && this.pointTop<top+10 && this.pointLeft+19>left && this.pointLeft<left+64)
  }

  checkIfCollideX = () => {
    return (this.state.brickWall
      .find( item => {
        return this.generateIfCollideX(item.left, item.top)===true
      }))
  }

  checkIfCollideY = () => {
    return (this.state.brickWall
      .find( item => {
        return this.generateIfCollideY(item.left, item.top)===true
      }))
  }

  moovingBall = () => {
    this.goRight ? this.pointLeft+=1 : this.pointLeft-=1
    this.goDown ? this.pointTop+=1 : this.pointTop-=1

    if (this.pointLeft > 355 || this.pointLeft < 0 || this.checkIfCollideX()
      ){
        this.goRight=!this.goRight
        this.deleteBricks()
    }

    if (this.pointTop > 587 || this.pointTop < 0 || this.checkIfCollideY()
    ){
        this.goDown=!this.goDown
        this.deleteBricks()
    }
  }

  displayBall=()=>{
    this.setState({displayLeft: this.pointLeft, displayTop: this.pointTop})
    setTimeout(this.displayBall, 100)
  }

  getBrickWall = () => {
    const brick = [];
        for (let i = 0; i < 6; i++) {     
            for (let j = 0; j < 5; j++){
              brick.push ({top:i*25, left:j*77})
            } 
        }
    return brick;
  };

  componentDidMount(){
    this.displayBall()
  }

  render(){
    const {displayLeft, displayTop} = this.state
    return (
      <div className="Game">
        <div style={{position: 'relative', height:'600px', width:'375', top:'67px'}}>
          {this.state.brickWall.map( item => {
            return(
              <Bricks 
              top={item.top}
              left={item.left}
              key={item.top+'-'+item.left}
              />
            ); 
          })}
        <Point left={displayLeft} top={displayTop}/>
        <Pad/>
        </div>
      </div>
    );
  }
}

export default Game;
