import React, { Component } from 'react';
import Bricks from './Bricks';


class Line extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }

    componentDidMount=()=>{
        this.props.parentCallback(this.bricksMatrix())
    }

    bricksMatrix = () => {
        const bricks = [];
            for (let i = 0; i < 6; i++) {     
                for (let j = 0; j < 6; j++){
                   bricks.push (<Bricks id={i*20+'-'+j*60} top={i*20} left={j*60} key={i*20+'-'+j*60}/>) 
                } 
            }
        return bricks;
    };

}

export default Line;
