import React, { Component } from "react";
import './MoveBart.css';
class Move extends Component {
  constructor(props) {
    super(props)
    this.goRight = true;
    this.state = {}
  }

  reverseBart = () => {
    if(this.props.left === 374-35) this.goRight=false;
    if(this.props.left === 35) this.goRight=true;

  }

  componentDidMount=()=>{

  }

  render() {
    const { left } = this.props;
    return (

      <div style={{ transform: this.goRight ? '' : 'scaleX(-1)' }}>
        <>
          <div
            className="bart"
            style={{
              left: `${left}px`,
            }}
          />
        </>
      </div>

    );

  }
}

export default Move;
