        super(props)
        this.state = {
        }
    }

    render(){
        const style = {
            width: "20px",
            height: "20px",
            background: `radial-gradient(white, #9198e5)`,
            borderRadius: "50%",
            position:'absolute',
            // transition: 'all linear 100ms'
        }
        const {left, top, move} = this.props

        return(
            <div style={{...style, left:left, top:top, transition: move ? 'all linear 10ms' : 'none' }}/>
        )
    }

}

export default Point
