import React, {Component} from 'react';
import donuts from './images/Donuts.png'

class Bonus extends Component {
    constructor(props){
        super(props)
        this.style={
            width:'50px',
            height:'50px',
            position:'absolute'
        }
        this.state={
            top:this.props.top,
            display:'block'
        }
    }

    falling=()=>{
        if(this.state.top<587){
            this.setState({top: this.state.top+3})
        }else{
            this.setState({display: 'none'})
        }
        setTimeout(this.falling, 15)
    }

    isBonusCollide = () => {
        if (this.state.top > 472
          && this.state.top < 474
          && this.props.left + 10 > this.props.padX
          && this.props.left - 10 < this.props.padX + this.props.pad) {
          console.log('collision');}
      }

    componentDidMount=()=>{
        this.falling();
        // () => this.props.callback(this.state.top, this.props.left)
    }

    render(){
        const {left}=this.props
        const {top, display}=this.state
        return(
            <>
                <img style={{...this.style, top:top , left:left, display:display}}
                    src={donuts}
                    alt="Donuts"
                    />
            </>
        )
    }
}

export default Bonus
