import React, {Component} from 'react';
import './Game.css';
import Point from './Point';
import Bricks from './Bricks';
import Pad from './Pad';

class Game extends Component {
  constructor(props) {
    super(props)
    this.pointLeft = 20
    this.pointTop = 587
    this.goRight = true
    this.goDown = false
    this.xSpeed = 1
    this.ySpeed = 1
    this.state = {
      brickWall: this.getBrickWall(),
      time:0
    }
  }

  componentDidMount=()=>{
    this.actualisRender()
  }

  actualisRender=()=>{

  }

  generateIfCollideX = (left, top) => {
    return (this.pointTop+19>top && this.pointTop<top+9 && this.pointLeft+20>left && this.pointLeft<left+65)
  }

  generateIfCollideY = (left, top) => {
    return (this.pointTop+20>top && this.pointTop<top+10 && this.pointLeft+19>left && this.pointLeft<left+64)
  }

  checkIfCollideX = () => {
    return (this.state.brickWall
      .find( item => {
        return this.generateIfCollideX(item.left, item.top)===true
      }))
  }

  checkIfCollideY = () => {
    return (this.state.brickWall
      .find( item => {
        return this.generateIfCollideY(item.left, item.top)===true
      }))
  }

  moovingBall = () => {
    this.goRight ? this.pointLeft+=this.xSpeed : this.pointLeft-=this.xSpeed
    this.pointLeft > 355 || this.pointLeft < 0 || this.checkIfCollideX() ? this.goRight=!this.goRight : this.goRight=this.goRight

    this.goDown ? this.pointTop+=this.ySpeed : this.pointTop-=this.ySpeed
    this.pointTop > 587 || this.pointTop < 0 || this.checkIfCollideY() ? this.goDown=!this.goDown : this.goDown=this.goDown
  }

  getBrickWall = () => {
    const brick = [];
        for (let i = 0; i < 6; i++) {     
            for (let j = 0; j < 5; j++){
              brick.push ({top:i*25, left:j*77})
            } 
        }
    return brick;
  };

  render(){
    setInterval(this.moovingBall(), 200)
    this.setState({time: this.state.time+=1})
    return (
      <div className="Game">
        <div style={{position: 'relative', height:'600px', width:'375', top:'67px'}}>
          {this.state.brickWall.map( item => {
            return(
              <Bricks 
              top={item.top}
              left={item.left}
              />
            ); 
          })}
        <Point left={this.pointLeft} top={this.pointTop}/>
        <Pad/>
        </div>
      </div>
    );
  }
}

export default Game;
